import classes from './Auth.module.css';
import { useDispatch } from 'react-redux';
import { authActions } from '../store';
import useInput from '../hooks/use-input';

const Auth = () => {

  let regexEmail = /([a-zA-Z0-9]+)([\.{1}])?([a-zA-Z0-9]+)\@gmail([\.])com/g;
  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasErrors: emailHasErrors,
    inputBlurHandler: emailInputBlurHandler,
    inputChangeHandler: emailInputChangedHandler,
    resetInput: resetEmailInput

  } = useInput(value => value.trim() !== '' && regexEmail.test(enteredEmail));

  const dispatch = useDispatch();
  const loginHandler = event => {
    dispatch(authActions.login);
  };

  return (
    <main className={classes.auth}>
      <section>
        <form>
          <div className={classes.control}>
            <label htmlFor='email'>Email</label>
            <input type='email' id='email' />
          {hasErrors && <p>Email is required</p>}
          </div>
          <div className={classes.control}>
            <label htmlFor='password'>Password</label>
            <input type='password' id='password' />
          </div>
          <button onClick={loginHandler}>Login</button>
        </form>
      </section>
    </main>
  );
};

export default Auth;
