import { useState } from "react";

const SimpleInput = (props) => {

  const regexEmail = /([a-zA-Z0-9]+)([\.{1}])?([a-zA-Z0-9]+)\@gmail([\.])com/g;


  const [enteredName, setEnteredName] = useState("");
  const [nameInputTouched, setNameInputTouched] = useState(false);
  const [enteredEmail, setEnteredEmail] = useState("");
  const [emailInputTouched, setEmailInputTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== '' && nameInputTouched;
  const nameInputIsInvalid = !enteredNameIsValid && nameInputTouched;
  const enteredEmailIsValid = enteredEmail.trim() !== '' && emailInputTouched;
  const emailInputIsInvalid = !enteredEmailIsValid && regexEmail.test(enteredEmail) && emailInputTouched;


  let formIsValid = false;

  if (enteredNameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  }

  const nameInputChangeHandler = (event) => {
    setNameInputTouched(true);
    setEnteredName(event.target.value);

  };

  const nameInputBlurHandler = event => {
    setNameInputTouched(true);
    if (!enteredNameIsValid) {
      return;
    }
  }

  const mailInputChangeHandler = event => {
      setEmailInputTouched(true);
      setEnteredEmail(event.target.value);
  }

  const mailInputBlurHandler = event => {
    setEmailInputTouched(true);
    if (!enteredEmailIsValid) {
      return;
    }
  }

  const onSubmitHandler = (event) => {
    event.preventDefault();
    setNameInputTouched(true);

    if (!enteredNameIsValid) {
       return;
    }

    setEnteredName("");
    setNameInputTouched(false);
  };

  const nameInputClasses = !nameInputIsInvalid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={onSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && <p className="error-text">Name is required</p>}
      </div>
      <div className={mailInputClasses}>
        <label htmlFor="email">Your Name</label>
        <input
          type="text"
          id="email"
          onChange={mailInputChangeHandler}
          onBlur={mailInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && <p className="error-text">Name is required</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
